
public class Ex5 {

	public static void returnToLowerCase(String s) {
		String lower = s.toLowerCase();
		System.out.println("The initial String that you have entered is: "+s+" and the String converted in lowercase is: " + lower);
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		returnToLowerCase("EIFELL");
	}

}
